import { DetectedMode, SessionSummary, JournalEntry } from "../../types/thinking";
import { useState, useEffect } from "react";
import { journalEntries as seededJournal } from "../../data/journal";

interface RightPanelProps {
  mode?: DetectedMode;
  summary?: SessionSummary | null;
}

const MODE_HINTS: Record<
  DetectedMode,
  {
    label: string;
    description: string;
    suggestions: string[];
  }
> = {
  exploring_options: {
    label: "Exploration",
    description: "You're clarifying ideas and understanding the problem space.",
    suggestions: [
      "Ask why this problem matters",
      "Challenge your assumptions",
      "Explore alternative angles",
    ],
  },
  overwhelmed: {
    label: "Overwhelmed",
    description: "You're feeling overloaded or uncertain about where to start.",
    suggestions: [
      "Identify the smallest next step",
      "Prioritize ruthlessly",
      "Take a short break and return with fresh eyes",
    ],
  },
  reflective: {
    label: "Reflection",
    description: "You're processing past actions or thoughts.",
    suggestions: [
      "What worked well?",
      "What would you change?",
      "What pattern do you notice?",
    ],
  },
  decisive: {
    label: "Decision Making",
    description: "You're evaluating options and moving toward a choice.",
    suggestions: [
      "List pros and cons",
      "Define success criteria",
      "Identify irreversible decisions",
    ],
  },
};

export default function RightPanel({ mode, summary }: RightPanelProps) {
  const modeConfig = mode ? MODE_HINTS[mode] : null;
  const [openJournal, setOpenJournal] = useState(false);
  const [openFull, setOpenFull] = useState(false);
  const [entries, setEntries] = useState<JournalEntry[]>([]);
  const [selectedEntry, setSelectedEntry] = useState<JournalEntry | null>(null);

  useEffect(() => {
    // load from localStorage if present, otherwise use seeded entries
    try {
      const raw = localStorage.getItem("ai_journal_entries");
      if (raw) {
        setEntries(JSON.parse(raw));
      } else {
        setEntries(seededJournal);
      }
    } catch (e) {
      setEntries(seededJournal);
    }
  }, []);

  const latestEntry = entries[0] ?? null;

  function persistEntries(next: JournalEntry[]) {
    setEntries(next);
    try { localStorage.setItem("ai_journal_entries", JSON.stringify(next)); } catch (e) { /* ignore */ }
  }

  return (
    <aside className="right-panel">
      <section className="right-panel__section">
        <div className="info-tile current-state">
          <div className="current-state-head">
            <div>
              <div className="phase-badge">{modeConfig ? modeConfig.label : "â€”"}</div>
              <h3>Current State</h3>
            </div>
            <div className="state-ornament" aria-hidden>
              {/* decorative SVG */}
              <svg width="54" height="54" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 2v6l4 2" stroke="rgba(123,97,255,0.18)" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round" />
                <circle cx="12" cy="12" r="9" stroke="rgba(123,97,255,0.06)" strokeWidth="1.2" />
              </svg>
            </div>
          </div>

          <div className="info-body">
            {modeConfig ? (
              <div className="desc-block">
                <div className="desc-text">{modeConfig.description}</div>
              </div>
            ) : (
              <p className="muted">No active thinking mode yet</p>
            )}

            {summary && summary.key_points.length > 0 && (
              <div className="info-section uncovered">
                <h4>What weâ€™ve uncovered</h4>
                <ul className="uncovered-list">
                  {summary.key_points.map((p, i) => (
                    <li key={i}><span className="li-icon" aria-hidden>âš‘</span><span>{p}</span></li>
                  ))}
                </ul>
              </div>
            )}

            {modeConfig && (
              <div className="info-section suggestions">
                <h4>Suggested next steps</h4>
                <div className="suggestion-chips">
                  {modeConfig.suggestions.map((s, i) => (
                    <button key={i} className="suggestion-chip" onClick={() => alert(s + ' (demo)')}>
                      <span className="chip-dot" aria-hidden>â€¢</span>
                      {s}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* summary and suggestions have been moved into the Current State info tile */}

      {/* Journal tile */}
      <section className="right-panel__section">
        <h4>Your Journal</h4>
        <div className="journal-tile" role="button" onClick={() => setOpenJournal((s) => !s)} aria-pressed={openJournal} tabIndex={0} onKeyDown={(e) => { if (e.key === 'Enter' || e.key === ' ') setOpenJournal(v => !v); }}>
          <div className="journal-icon" aria-hidden>ðŸ“”</div>
          <div className="journal-meta">
            <div className="journal-title">{latestEntry?.title ?? 'No entries yet'}</div>
            <div className="muted">{latestEntry ? new Date(latestEntry.created_at).toLocaleDateString() : ''}</div>
          </div>
        </div>

        {openJournal && latestEntry && (
          <div className="diary-panel" role="region" aria-label="Journal entry">
            <div className="diary-header">
              <div className="diary-title">{latestEntry.title}</div>
              <div className="diary-date muted">{new Date(latestEntry.created_at).toLocaleString()}</div>
            </div>
            <div className="diary-body">
              {latestEntry.content.split('\n').map((line, idx) => (
                <div key={idx} className="diary-line">{line}</div>
              ))}
            </div>
            <div className="diary-actions">
              <button className="prompt-btn" onClick={() => { setOpenFull(true); setSelectedEntry(null); }}>Open Full Journal</button>
              <button className="prompt-btn" onClick={() => { setOpenFull(true); setSelectedEntry(null); }}>New Entry</button>
            </div>
          </div>
        )}
      </section>

      {openFull && (
        <div className="modal-backdrop" role="dialog" aria-modal="true">
          <div className="modal full-journal">
            <div className="modal-header">
              <h3>Your Journal</h3>
              <button className="prompt-btn" onClick={() => setOpenFull(false)}>Close</button>
            </div>

            <div className="modal-body">
              <div className="journal-list">
                <div className="journal-list-header muted">Entries</div>
                <ul>
                  {entries.map((e) => (
                    <li key={e.entry_id} className={`journal-list-item ${selectedEntry?.entry_id === e.entry_id ? 'active' : ''}`} onClick={() => setSelectedEntry(e)}>
                      <div className="jl-title">{e.title}</div>
                      <div className="jl-date muted">{new Date(e.created_at).toLocaleString()}</div>
                    </li>
                  ))}
                </ul>
                <div className="journal-new">
                  <h4>New Entry</h4>
                  <JournalForm onSave={(entry) => {
                    const next = [entry, ...entries];
                    persistEntries(next);
                    setSelectedEntry(entry);
                  }} />
                </div>
              </div>

              <div className="journal-preview">
                {selectedEntry ? (
                  <div className="notebook-card">
                    <div className="note-head"><strong>{selectedEntry.title}</strong><div className="muted">{new Date(selectedEntry.created_at).toLocaleString()}</div></div>
                    <div className="note-body">
                      {selectedEntry.content.split('\n').map((ln, i) => (
                        <div key={i} className="note-line">{ln}</div>
                      ))}
                    </div>
                  </div>
                ) : (
                  <div className="muted">Select an entry or create a new one.</div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </aside>
  );
}

// Small form component for creating a journal entry
function JournalForm({ onSave }: { onSave: (e: JournalEntry) => void }) {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  function handleSave() {
    if (!title.trim() && !content.trim()) return;
    const entry: JournalEntry = {
      entry_id: `j${Date.now()}`,
      linked_session_id: "",
      title: title.trim() || 'Untitled',
      content: content.trim(),
      created_at: new Date().toISOString(),
      source: 'user_written',
    };
    onSave(entry);
    setTitle("");
    setContent("");
  }

  return (
    <div>
      <input className="journal-input" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
      <textarea className="journal-textarea" placeholder="Write your entry..." value={content} onChange={(e) => setContent(e.target.value)} />
      <div style={{display: 'flex', gap: 8}}>
        <button className="prompt-btn" onClick={handleSave}>Save Entry</button>
      </div>
    </div>
  );
}
