import { Thread, Session, UserPreferences } from "../../types/thinking";
import { useState } from "react";

interface ChatPanelProps {
  activeThread?: Thread | null;
  session?: Session | null;
  preferences: UserPreferences;
}

export default function ChatPanel({
  activeThread,
  session,
  preferences,
}: ChatPanelProps) {
  const [input, setInput] = useState("");

  type Message = { role: "user" | "assistant"; content: string };

  const [messages, setMessages] = useState<Message[]>([]);

  // When the session changes (thread switch), load any historical messages
  // from the session into local state so the user sees previous chat.
  useEffect(() => {
    if (session?.messages && session.messages.length > 0) {
      setMessages(session.messages as Message[]);
    } else {
      setMessages([]);
    }
  }, [session]);

  const handleSend = () => {
    if (!input.trim()) return;

    // 1️⃣ Add user message
    const userMessage: Message = { role: "user", content: input };
    setMessages((prev) => [...prev, userMessage]);

    // 2️⃣ Fake assistant response (for now)
    const assistantMessage: Message = {
      role: "assistant",
      content: getFakeResponse(input, preferences),
    };

    setMessages((prev) => [...prev, assistantMessage]);
    setInput("");
  };

  return (
    <div className="chat-panel">
      {/* Context header */}
      <div className="chat-header">
        <h3>{activeThread?.title ?? "Thinking space"}</h3>
        {/* Mode label removed: not editable by user in this UI */}
      </div>

      {/* Messages */}
      <div className="messages">
        {messages.map((m, idx) => (
          <div key={idx} className={`msg ${m.role}`}>
            {m.content}
          </div>
        ))}
      </div>

      {/* Input */}
      <div className="chat-input">
        <textarea
          id="chat-input"
          name="chat_input"
          aria-label="Chat input"
          value={input}
          placeholder="Think out loud…"
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}
function getFakeResponse(
  input: string,
  preferences: UserPreferences
): string {
  if (preferences.default_tone === "direct") {
    return "Let’s simplify this. What decision are you actually trying to make?";
  }

  if (preferences.depth_level === "deep") {
    return "Let me reflect this back. It sounds like you’re torn between safety and growth. What’s driving that tension right now?";
  }

  return "I’m hearing a few competing thoughts here. Want to slow down and look at them one by one?";
}
