import React, { useState } from "react";
import { Thread } from "../../types/thinking";
import { currentUser, User } from "../../data/userDb";

interface LeftPanelProps {
  threads: Thread[];
  activeThreadId?: string;
  onThreadSelect: (id: string) => void;
}

export default function LeftPanel({
  threads,
  activeThreadId,
  onThreadSelect,
}: LeftPanelProps) {
  const [showSettings, setShowSettings] = useState(false);

  function renderUserInfo(user: User) {
    return (
      <div className="user-info-panel" role="dialog" aria-label="User settings">
        <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
          {user.avatarUrl && (
            // eslint-disable-next-line @next/next/no-img-element
            <img src={user.avatarUrl} alt={`${user.name} avatar`} style={{ width: 46, height: 46, borderRadius: 8 }} />
          )}
          <div>
            <div style={{ fontWeight: 600 }}>{user.name}</div>
            <div style={{ fontSize: 12, color: "var(--muted)" }}>{user.role}</div>
          </div>
        </div>

        <div style={{ marginTop: 10, fontSize: 13 }}>{user.bio}</div>
        <div style={{ marginTop: 10, fontSize: 13, color: "var(--muted)" }}>{user.email}</div>

        <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
          <button className="prompt-btn" onClick={() => alert("Edit profile (demo)")}>Edit Profile</button>
          <button className="prompt-btn" onClick={() => alert("Sign out (demo)")}>Sign out</button>
          <button style={{ marginLeft: "auto" }} className="prompt-btn" onClick={() => setShowSettings(false)}>Close</button>
        </div>
      </div>
    );
  }

  return (
    <aside className="left-panel">
      <div className="left-panel-inner">
        <h3>Active Threads</h3>
        {threads.map((t) => (
          <div
            key={t.thread_id}
            className={t.thread_id === activeThreadId ? "active" : ""}
            onClick={() => onThreadSelect(t.thread_id)}
          >
            {t.title}
          </div>
        ))}

        <div className="settings-container">
          <button className="settings-btn" onClick={() => setShowSettings((s) => !s)} aria-expanded={showSettings} aria-controls="user-settings">
            ⚙️ Settings
          </button>
        </div>
      </div>

      {showSettings && (
        <div id="user-settings">
          {renderUserInfo(currentUser)}
        </div>
      )}
    </aside>
  );
}
