import { useThreads } from "../hooks/useThreads";
import { useCurrentSession } from "../hooks/useCurrentSession";
import { useSessionSummary } from "../hooks/useSessionSummary";
import { useUserPreferences } from "../hooks/useUserPreferences";
import React from "react";
import LeftPanel from "../components/layout/LeftPanel";
import ChatPanel from "../components/chat/ChatPanel";
import RightPanel from "../components/layout/RightPanel";
import MobileLanding from "./MobileLanding";
import { useState, useEffect } from 'react';

export default function ThinkingPartnerPage() {
  // ðŸ§  STATE OWNERSHIP LIVES HERE
  const {
    threads,
    activeThread,
    setActiveThreadId,
    addThread,
  } = useThreads();

  const { session } = useCurrentSession(activeThread?.thread_id);
  const { summary } = useSessionSummary(session?.session_id);
  const preferences = useUserPreferences();

  const [isMobile, setIsMobile] = useState<boolean>(false);
  const [mobileEntered, setMobileEntered] = useState<boolean>(false);
  const [mobileRightOpen, setMobileRightOpen] = useState<boolean>(false);

  useEffect(() => {
    const check = () => setIsMobile(typeof window !== 'undefined' ? window.innerWidth <= 768 : false);
    check();
    window.addEventListener('resize', check);
    return () => window.removeEventListener('resize', check);
  }, []);

  return (
    <div>
      {isMobile && !mobileEntered ? (
        <MobileLanding threads={threads} onSelect={(tid) => { setActiveThreadId(tid); setMobileEntered(true); }} />
      ) : (
        <div className="app-layout">
          <LeftPanel
            threads={threads}
            activeThreadId={activeThread?.thread_id}
            onThreadSelect={setActiveThreadId}
            onCreateThread={(title, desc) => addThread(title, desc)}
          />

          <ChatPanel
            activeThread={activeThread}
            session={session}
            preferences={preferences}
            onOpenRight={() => setMobileRightOpen(true)}
          />

          <RightPanel
            mode={session?.detected_mode}
            summary={summary}
          />

          {/* Mobile overlay for right panel (when hamburger pressed) */}
          {isMobile && mobileRightOpen ? (
            <div className="mobile-right-overlay">
              <div className="overlay open" onClick={() => setMobileRightOpen(false)} />
              <div className="mobile-right-drawer">
                <RightPanel mode={session?.detected_mode} summary={summary} />
                <button className="prompt-btn" onClick={() => setMobileRightOpen(false)}>Close</button>
              </div>
            </div>
          ) : null}
        </div>
      )}
    </div>
  );
}
