import React from 'react';
import LeftPanel from '../components/layout/LeftPanel';

interface MobileLandingProps {
  threads: Array<{ thread_id: string; title: string; description?: string }>;
  onSelect: (threadId: string) => void;
  onCreate?: (title: string, desc?: string) => void;
  activeThreadId?: string | undefined;
}

export default function MobileLanding({ threads, onSelect, onCreate, activeThreadId }: MobileLandingProps) {
  // Show up to 4 tiles — if there are fewer, fill with placeholders
  const tiles = (threads || []).slice(0, 4);
  while (tiles.length < 4) tiles.push({ thread_id: `t_gap_${tiles.length}`, title: 'Start a thread', description: '' });

  return (
    <div className="mobile-landing">
      {/* Reuse LeftPanel styling/structure to show threads on page1 */}
      <aside style={{ marginBottom: 16 }}>
        <LeftPanel
          threads={threads}
          activeThreadId={activeThreadId}
          onThreadSelect={(id) => onSelect(id)}
          onCreateThread={(t, d) => onCreate && onCreate(t, d)}
        />
      </aside>

      <div className="mobile-landing-header">
        <h2>Welcome to Altar</h2>
        <p className="muted">Use this space to untangle thoughts, plan, and keep a private journal.</p>
      </div>

      <div className="landing-grid" role="grid" aria-label="Quick start tiles">
        {tiles.map((t, i) => (
          <button key={t.thread_id + i} className="landing-tile" onClick={() => onSelect(t.thread_id)}>
            <div className="tile-title">{t.title}</div>
            {t.description ? <div className="tile-desc muted">{t.description}</div> : null}
          </button>
        ))}
      </div>

      <div className="landing-actions">
        <button className="prompt-btn" onClick={() => window.dispatchEvent(new CustomEvent('open_signup_modal', { detail: { mode: 'login' } }))}>Sign in</button>
        <button className="prompt-btn" onClick={() => window.dispatchEvent(new CustomEvent('open_signup_modal', { detail: { mode: 'create' } }))}>Create account</button>
      </div>
    </div>
  );
}
