import React, { useState, useRef, useEffect } from "react";
import "./App.css";

function App() {
  const [message, setMessage] = useState("");
  const [conversation, setConversation] = useState([
    { role: "assistant", content: "Welcome back! How are you feeling today?" },
    { role: "user", content: "I’m feeling anxious about work." },
    { role: "assistant", content: "I hear you. Want to talk about it?" },
  ]);
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);
  const [typing, setTyping] = useState(false);

  const chatEndRef = useRef(null);

  // Auto-scroll
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [conversation, typing]);

  // Mock user info
  const user = {
    name: "Ankur",
    subscription: "Free trial",
    credits: 15, // minutes
  };

  const sendMessage = async () => {
    if (!message.trim()) return;

    const userMessage = { role: "user", content: message };
    setConversation((prev) => [...prev, userMessage]);
    setMessage("");
    setLoading(true);
    setTyping(true);

    try {
      const res = await fetch("http://127.0.0.1:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: message }),
      });
      const data = await res.json();

      const botMessage = { role: "assistant", content: data.reply };
      setConversation((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
      setTyping(false);
    }
  };

  const getSummary = async () => {
    if (conversation.length === 0) return;

    setLoading(true);
    try {
      const res = await fetch("http://127.0.0.1:8000/summarize", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ conversation }),
      });
      const data = await res.json();
      setSummary(data.summary);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      {/* Header / account info */}
      <header className="app-header">
        <h2>Therapy Chat</h2>
        <div className="user-info">
          <span>{user.name}</span> | <span>{user.subscription}</span> |{" "}
          <span>{user.credits} min</span>
        </div>
      </header>

      {/* Chat box */}
      <div className="chat-box">
        {conversation.map((msg, index) => (
          <div
            key={index}
            className={msg.role === "user" ? "user-msg" : "bot-msg"}
          >
            <b>{msg.role === "user" ? "You" : "Bot"}:</b> {msg.content}
          </div>
        ))}
        {typing && <div className="bot-msg typing">Bot is typing...</div>}
        <div ref={chatEndRef} />
      </div>

      {/* Input area */}
      <div className="input-area">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={sendMessage} disabled={loading}>
          Send
        </button>
        <button onClick={getSummary} disabled={loading}>
          Get Summary
        </button>
      </div>

      {/* Summary box */}
      {summary && (
        <div className="summary-box">
          <h3>Summary</h3>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}

export default App;
