import React, { useEffect, useState } from "react";
import "./toast.css";

type ToastItem = { id: number; message: string; kind?: "success" | "error" | "info" | "warn" };

export default function ToastManager() {
  const [toasts, setToasts] = useState<ToastItem[]>([]);

  useEffect(() => {
    function handler(e: any) {
      const { message, kind } = e.detail || {};
      const id = Date.now() + Math.floor(Math.random() * 1000);
      const item = { id, message: String(message), kind: kind || "info" };
      setToasts((t) => [...t, item]);
      // auto-dismiss
      setTimeout(() => setToasts((t) => t.filter((x) => x.id !== id)), 4200);
    }
    window.addEventListener("ai_toast", handler as EventListener);
    return () => window.removeEventListener("ai_toast", handler as EventListener);
  }, []);

  return (
    <div className="ai-toast-root" aria-live="polite">
      {toasts.map((t) => (
        <div key={t.id} className={`ai-toast ${t.kind || "info"}`} role="status">
          {t.message}
        </div>
      ))}
    </div>
  );
}
